import resizeChart from "@/mixins/resizeChart";
import resizeWindow from "@/mixins/resizeWindow";
export default {
  name: "BarSimpleChart",
  mixins: [resizeChart, resizeWindow],
  props: {
    color: {
      type: String,
      default: ''
    }
  },

  data() {
    const self = this;
    return {
      barOptions: {
        grid: {
          left: '0%',
          right: '40%',
          bottom: '2%',
          top: '3%',
          containLabel: true
        },
        xAxis: {
          axisLabel: {
            fontSize: 10,
            interval: 0,
            formatter: '{value}'
          },
          type: 'category',
          data: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun']
        },
        yAxis: {
          type: 'value',
          max: 800,
          position: 'right'
        },
        series: [{
          barWidth: 20,
          itemStyle: {
            color: '#6A38EB',
            barBorderRadius: [5, 5, 0, 0],
            borderWidth: 1,
            borderType: 'solid'
          },
          data: [120, 400, 150, 80, 70, 110, 130],
          type: 'bar'
        }]
      }
    };
  },

  watch: {
    windowSize: {
      handler(val) {
        if (val < 996) {
          this.barOptions.grid.right = '0';
        } else this.barOptions.grid.right = '40%';
      },

      deep: true
    }
  },

  mounted() {
    this.setRefElement('barChart');
  }

};
