import ListStats from "@/components/universal/ListStats";
import AccordionItem from "@/components/pages/company/tabs/stock-analysis/operations/AccordionItem";
export default {
  components: {
    AccordionItem,
    ListStats
  },
  props: {
    items: {
      type: Array,
      default: () => []
    }
  },
  data: () => ({
    accordionItems: []
  }),
  methods: {
    toggleItem(id) {
      this.accordionItems = this.accordionItems.map(item => item.id === id && !item.open ? { ...item,
        open: true
      } : { ...item,
        open: false
      });
    }

  },

  mounted() {
    this.accordionItems = this.items.map((item, i) => !i ? { ...item,
      open: true
    } : { ...item,
      open: false
    });
  }

};
